package day02;

public class Oper4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/*
		 == : 같다
		 !=	: 다르다
		 <	: 왼쪽이 작다
		 >	: 왼쪽이 크다
		 <=	: 작거나 같다
		 >= : 크거나 같다
		 
		 비교 연산자는 왼쪽과 오른쪽을 비교하고 결과를 true 쪽은 false로 열어줍니다 (boolean type) 
		 */
		int num = 10;
		 System.out.println(num == 10);
		 System.out.println(num != 10);
		 System.out.println(num < 10);
		 System.out.println(num > 10);
		 System.out.println(num <= 10);
		 System.out.println(num >= 10);
		
	}

}
